import React, { useState, useEffect, useRef } from "react";
import { Routes, Route, Link } from "react-router-dom";
import { motion, useMotionValue, useSpring } from "framer-motion";

import Home from "./pages/Home";
import DigitalLandscapes from "./pages/DigitalLandscapes";
import PlaylistLocalization from "./pages/PlaylistLocalization";
import WebVerifyExpansion from "./pages/WebVerifyExpansion";
import UnifiedAuth from "./pages/UnifiedAuth";

export default function App() {
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("theme") === "dark";
  });
  const [flippedCards, setFlippedCards] = useState({});

  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);

  const springX = useSpring(mouseX, { stiffness: 100, damping: 30 });
  const springY = useSpring(mouseY, { stiffness: 100, damping: 30 });

  // Create separate springs for each blob with different characteristics
  const pinkSpringX = useSpring(mouseX, { stiffness: 80, damping: 25 });
  const pinkSpringY = useSpring(mouseY, { stiffness: 80, damping: 25 });
  const blueSpringX = useSpring(mouseX, { stiffness: 90, damping: 35 });
  const blueSpringY = useSpring(mouseY, { stiffness: 90, damping: 35 });
  const purpleSpringX = useSpring(mouseX, { stiffness: 70, damping: 20 });
  const purpleSpringY = useSpring(mouseY, { stiffness: 70, damping: 20 });

  useEffect(() => {
    localStorage.setItem("theme", darkMode ? "dark" : "light");
  }, [darkMode]);

  const handleMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    mouseX.set((e.clientX - rect.left - centerX) * 0.15);
    mouseY.set((e.clientY - rect.top - centerY) * 0.15);
  };

  const handleCardFlip = (cardId) => {
    setFlippedCards((prev) => ({
      ...prev,
      [cardId]: !prev[cardId],
    }));
  };

  const caseStudyData = {
    "digital-landscapes": {
      title: "Digital Landscapes Redesign",
      company: "Walmart Â· 2024â€“2025",
      summary:
        "Redesigned a key internal tool for supplier performance data, improving navigation and user experience with scalable components.",
      metric: "+10.8% adoption increase",
      details: {
        context:
          "Digital Landscapes is a key internal tool for supplier performance data. Users struggled to navigate datasets, apply filters, or compare metrics.",
        challenges: [
          "Navigation issues hindered dataset discovery",
          "Limited interactivity for filtering and comparison",
          "Insights lacked context and clarity",
        ],
        solution: [
          "Scalable information architecture and entry points",
          "Reusable chart card components with contextual controls",
          "Design ops innovations for dynamic handoff",
        ],
        impact: [
          "+10.8% increase in tool adoption",
          "9 reusable components, 4 chart types, 2 templates delivered",
          "Greater stakeholder engagement and trust in design",
        ],
      },
    },
    "playlist-localization": {
      title: "Playlist Localization Tool",
      company: "Spotify Â· 2021â€“2022",
      summary:
        "Built internal tooling to help global music editors localize playlists at scale.",
      metric: "Global content scaling",
      details: {
        context:
          "Spotify needed to scale playlist localization across multiple markets while maintaining editorial quality and cultural relevance.",
        challenges: [
          "Manual processes didn't scale globally",
          "Inconsistent localization quality",
          "Editors needed better workflow tools",
        ],
        solution: [
          "Automated workflow system",
          "Quality assurance checkpoints",
          "Collaborative editing interface",
        ],
        impact: [
          "Reduced localization time by 60%",
          "Improved content quality scores",
          "Enabled expansion to 15 new markets",
        ],
      },
    },
    "webverify-expansion": {
      title: "WebVerify i18n Expansion",
      company: "CLEAR Â· 2021",
      summary:
        "Launched verification in Mexico and Canada with localized document handling and improved UX.",
      metric: "98.8% error reduction",
      details: {
        context:
          "CLEAR needed to launch verification in Mexico and Canada within 3 months. Canada had document misuse; Mexico required manual CURP input prone to errors.",
        challenges: [
          "CURP formatting and accuracy issues in Mexico",
          "High rate of invalid uploads in Canada (e.g. health cards)",
        ],
        solution: [
          "Self-attestation MVP to meet contractual deadline",
          "Localized doc selector and backend API mapping",
          "Improved UX around document validity upfront",
        ],
        impact: [
          "Reduced Canadian health card uploads by 98.8%",
          "10% of weekly verification volume from MX & CA",
          "Better doc routing, fewer API errors and costs",
        ],
      },
    },
    "unified-auth": {
      title: "Unified Authentication",
      company: "CLEAR Â· 2023â€“2024",
      summary:
        "Streamlined authentication across multiple platforms and user types.",
      metric: "Cross-platform unification",
      details: {
        context:
          "CLEAR had fragmented authentication systems across different products, creating user confusion and operational complexity.",
        challenges: [
          "Multiple login systems created friction",
          "Inconsistent user experience",
          "Complex user management",
        ],
        solution: [
          "Single sign-on implementation",
          "Unified user interface design",
          "Streamlined onboarding flow",
        ],
        impact: [
          "Reduced support tickets by 40%",
          "Improved user satisfaction scores",
          "Simplified backend architecture",
        ],
      },
    },
  };

  return (
    <div
      className={`min-h-screen overflow-hidden ${
        darkMode ? "bg-gray-900" : "bg-gray-50"
      }`}
      onMouseMove={handleMouseMove}
    >
      {/* Dark mode toggle */}
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="fixed top-6 right-6 z-50 w-12 h-12 rounded-full border border-current flex items-center justify-center hover:bg-current hover:text-white transition-all duration-300"
      >
        {darkMode ? "â˜€ï¸" : "ðŸŒ™"}
      </button>

      {/* Abstract shapes */}
      <div className="fixed inset-0 pointer-events-none">
        {/* Yellow blob */}
        <motion.div
          className="absolute w-[600px] h-[600px] rounded-full"
          style={{
            background: "linear-gradient(135deg, #f3e8a6 0%, #e6d875 100%)",
            top: "-20%",
            left: "-20%",
            x: springX,
            y: springY,
          }}
          animate={{
