import React from "react";

export default function WaveBackground({ darkMode }) {
  return (
    <div className="fixed inset-0 z-0 overflow-hidden pointer-events-none">
      {/* Back wave layer */}
      <svg
        viewBox="0 0 1440 320"
        preserveAspectRatio="none"
        className="absolute bottom-0 w-[300%] h-full animate-waveMotion opacity-60"
        style={{ animationDelay: "0s", animationDuration: "15s", left: "-50%" }}
      >
        <path
          fill={darkMode ? "#1a1a2e" : "#b3e5fc"}
          d="M0,192 C320,288 1120,96 1440,192 L1440,320 L0,320 Z"
        />
      </svg>

      {/* Middle wave layer */}
      <svg
        viewBox="0 0 1440 320"
        preserveAspectRatio="none"
        className="absolute bottom-0 w-[300%] h-full animate-waveMotion opacity-80"
        style={{
          animationDelay: "-5s",
          animationDuration: "12s",
          left: "-50%",
        }}
      >
        <path
          fill={darkMode ? "#16213e" : "#81d4fa"}
          d="M0,160 C480,320 960,0 1440,160 L1440,320 L0,320 Z"
        />
      </svg>

      {/* Front wave layer */}
      <svg
        viewBox="0 0 1440 320"
        preserveAspectRatio="none"
        className="absolute bottom-0 w-[300%] h-full animate-waveMotion"
        style={{
          animationDelay: "-2s",
          animationDuration: "10s",
          left: "-50%",
        }}
      >
        <path
          fill={darkMode ? "#0f3460" : "#4fc3f7"}
          d="M0,128 C360,256 1080,32 1440,128 L1440,320 L0,320 Z"
        />
      </svg>
    </div>
  );
}
