import React from "react";
import { motion } from "framer-motion";

export default function Portfolio() {
  return (
    <div className="min-h-screen bg-black text-white font-sans">
      <header className="h-screen flex flex-col items-center justify-center text-center px-4">
        <motion.h1
          className="text-6xl font-extrabold tracking-tight"
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          Yaz Liu
        </motion.h1>
        <motion.p
          className="mt-6 text-lg max-w-xl"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4, duration: 0.8 }}
        >
          Product designer focused on turning complexity into clarity. I build systems, elevate design ops, and scale meaningful experiences.
        </motion.p>
      </header>

      <main className="max-w-4xl mx-auto px-4 pb-24">
        <section className="mb-24">
          <motion.h2
            className="text-4xl font-bold mb-12"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            Case Studies
          </motion.h2>

          <div className="space-y-16">
            <CaseStudy
              title="Digital Landscapes Redesign"
              company="Walmart · 2024–2025"
              summary="Redesigned a key internal data tool, increasing adoption by 10.8% and laying platform foundations."
              highlights={[
                "Built scalable IA and 4 new chart types",
                "Created design ops practices for handoff",
                "Shaped system-wide architecture for insights"
              ]}
            />

            <CaseStudy
              title="Playlist Localization Tool"
              company="Spotify · 2021–2022"
              summary="Built internal tooling to help editors localize playlists, boosting engagement in 60+ markets."
              highlights={[
                "+95.6% impression-to-click",
                "Reduced localization time by 90%",
                "Enabled async, cross-team collaboration"
              ]}
            />

            <CaseStudy
              title="WebVerify i18n Expansion"
              company="CLEAR · 2021"
              summary="Launched verification in Mexico and Canada within 3 months, improving doc accuracy and reducing cost."
              highlights={[
                "Reduced Canadian health card uploads by 98.8%",
                "Launched localized doc selectors",
                "Improved backend verification accuracy"
              ]}
            />

            <CaseStudy
              title="Unified Authentication"
              company="CLEAR · 2023–2024"
              summary="Defined and designed a unified auth framework across consumer and partner tools."
              highlights={[
                "Achieved 99% login success via phone OTP",
                "Reduced account duplication to <1%",
                "Enabled HIPAA-compliant flows at Wellstar"
              ]}
            />
          </div>
        </section>
      </main>

      <footer className="text-center text-sm py-8 border-t border-gray-700">
        <p className="text-gray-400">&copy; 2025 Yaz Liu</p>
      </footer>
    </div>
  );
}

function CaseStudy({ title, company, summary, highlights }) {
  return (
    <motion.div
      className="border border-gray-700 p-6 rounded-2xl hover:bg-white hover:text-black transition-colors duration-300"
      initial={{ opacity: 0, y: 40 }}
      whileInView={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
      viewport={{ once: true }}
    >
      <h3 className="text-2xl font-semibold mb-1">{title}</h3>
      <p className="text-sm text-gray-400 mb-2">{company}</p>
      <p className="mb-4">{summary}</p>
      <ul className="list-disc list-inside space-y-1">
        {highlights.map((point, idx) => (
          <li key={idx}>{point}</li>
        ))}
      </ul>
    </motion.div>
  );
}
