import React, { useState, useEffect, useRef } from "react";
import { Routes, Route, Link } from "react-router-dom";
import { motion, useMotionValue, useSpring } from "framer-motion";

import Home from "./pages/Home";
import DigitalLandscapes from "./pages/DigitalLandscapes";
import PlaylistLocalization from "./pages/PlaylistLocalization";
import WebVerifyExpansion from "./pages/WebVerifyExpansion";
import UnifiedAuth from "./pages/UnifiedAuth";

export default function App() {
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("theme") === "dark";
  });

  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);

  const springX = useSpring(mouseX, { stiffness: 100, damping: 30 });
  const springY = useSpring(mouseY, { stiffness: 100, damping: 30 });

  useEffect(() => {
    localStorage.setItem("theme", darkMode ? "dark" : "light");
  }, [darkMode]);

  const handleMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    mouseX.set((e.clientX - rect.left - centerX) * 0.15);
    mouseY.set((e.clientY - rect.top - centerY) * 0.15);
  };

  return (
    <div
      className={`min-h-screen overflow-hidden ${
        darkMode ? "bg-gray-900" : "bg-gray-50"
      }`}
      onMouseMove={handleMouseMove}
    >
      {/* Dark mode toggle */}
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="fixed top-6 right-6 z-50 w-12 h-12 rounded-full border border-current flex items-center justify-center hover:bg-current hover:text-white transition-all duration-300"
      >
        {darkMode ? "☀️" : "🌙"}
      </button>

      {/* Abstract shapes */}
      <div className="fixed inset-0 pointer-events-none">
        {/* Yellow blob */}
        <motion.div
          className="absolute w-[600px] h-[600px] rounded-full"
          style={{
            background: "linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)",
            top: "-20%",
            left: "-20%",
          }}
          animate={{
            scale: [1, 1.2, 0.9, 1],
            rotate: [0, 10, -5, 0],
            x: [springX, springX.get() + 50, springX.get() - 30, springX],
            y: [springY, springY.get() - 40, springY.get() + 20, springY],
          }}
          transition={{
            duration: 15,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />

        {/* Pink blob */}
        <motion.div
          className="absolute w-[500px] h-[700px]"
          style={{
            background: "linear-gradient(45deg, #f9a8d4 0%, #ec4899 100%)",
            borderRadius: "60% 40% 30% 70% / 60% 30% 70% 40%",
            top: "10%",
            left: "-15%",
          }}
          animate={{
            borderRadius: [
              "60% 40% 30% 70% / 60% 30% 70% 40%",
              "30% 60% 70% 40% / 50% 60% 30% 60%",
              "70% 30% 60% 40% / 40% 70% 60% 30%",
              "60% 40% 30% 70% / 60% 30% 70% 40%",
            ],
            x: [
              useSpring(mouseX, {
                stiffness: 80,
                damping: 25,
                transform: (v) => v * -0.8,
              }),
              useSpring(mouseX, {
                stiffness: 80,
                damping: 25,
                transform: (v) => v * -0.8,
              }).get() - 60,
              useSpring(mouseX, {
                stiffness: 80,
                damping: 25,
                transform: (v) => v * -0.8,
              }).get() + 40,
              useSpring(mouseX, {
                stiffness: 80,
                damping: 25,
                transform: (v) => v * -0.8,
              }),
            ],
            y: [
              useSpring(mouseY, {
                stiffness: 80,
                damping: 25,
                transform: (v) => v * -0.6,
              }),
              useSpring(mouseY, {
                stiffness: 80,
                damping: 25,
                transform: (v) => v * -0.6,
              }).get() + 30,
              useSpring(mouseY, {
                stiffness: 80,
                damping: 25,
                transform: (v) => v * -0.6,
              }).get() - 50,
              useSpring(mouseY, {
                stiffness: 80,
                damping: 25,
                transform: (v) => v * -0.6,
              }),
            ],
            rotate: [0, -8, 12, 0],
          }}
          transition={{
            duration: 18,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />

        {/* Blue blob */}
        <motion.div
          className="absolute w-[550px] h-[600px]"
          style={{
            background: "linear-gradient(225deg, #93c5fd 0%, #3b82f6 100%)",
            borderRadius: "40% 60% 60% 40% / 60% 30% 70% 40%",
            bottom: "-10%",
            right: "-20%",
          }}
          animate={{
            borderRadius: [
              "40% 60% 60% 40% / 60% 30% 70% 40%",
              "60% 40% 40% 60% / 40% 60% 30% 70%",
              "50% 50% 50% 50% / 50% 50% 50% 50%",
              "40% 60% 60% 40% / 60% 30% 70% 40%",
            ],
            x: [
              useSpring(mouseX, {
                stiffness: 90,
                damping: 35,
                transform: (v) => v * 0.7,
              }),
              useSpring(mouseX, {
                stiffness: 90,
                damping: 35,
                transform: (v) => v * 0.7,
              }).get() + 80,
              useSpring(mouseX, {
                stiffness: 90,
                damping: 35,
                transform: (v) => v * 0.7,
              }).get() - 20,
              useSpring(mouseX, {
                stiffness: 90,
                damping: 35,
                transform: (v) => v * 0.7,
              }),
            ],
            y: [
              useSpring(mouseY, {
                stiffness: 90,
                damping: 35,
                transform: (v) => v * 0.5,
              }),
              useSpring(mouseY, {
                stiffness: 90,
                damping: 35,
                transform: (v) => v * 0.5,
              }).get() - 60,
              useSpring(mouseY, {
                stiffness: 90,
                damping: 35,
                transform: (v) => v * 0.5,
              }).get() + 40,
              useSpring(mouseY, {
                stiffness: 90,
                damping: 35,
                transform: (v) => v * 0.5,
              }),
            ],
            scale: [1, 0.8, 1.1, 1],
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />

        {/* Additional flowing blob */}
        <motion.div
          className="absolute w-[400px] h-[500px]"
          style={{
            background: "linear-gradient(180deg, #a78bfa 0%, #8b5cf6 100%)",
            borderRadius: "70% 30% 50% 50% / 40% 60% 40% 60%",
            top: "40%",
            right: "10%",
          }}
          animate={{
            borderRadius: [
              "70% 30% 50% 50% / 40% 60% 40% 60%",
              "30% 70% 40% 60% / 60% 40% 60% 40%",
              "50% 50% 70% 30% / 50% 50% 30% 70%",
              "70% 30% 50% 50% / 40% 60% 40% 60%",
            ],
            x: [
              useSpring(mouseX, {
                stiffness: 70,
                damping: 20,
                transform: (v) => v * -0.4,
              }),
              useSpring(mouseX, {
                stiffness: 70,
                damping: 20,
                transform: (v) => v * -0.4,
              }).get() - 40,
              useSpring(mouseX, {
                stiffness: 70,
                damping: 20,
                transform: (v) => v * -0.4,
              }).get() + 60,
              useSpring(mouseX, {
                stiffness: 70,
                damping: 20,
                transform: (v) => v * -0.4,
              }),
            ],
            y: [
              useSpring(mouseY, {
                stiffness: 70,
                damping: 20,
                transform: (v) => v * 0.8,
              }),
              useSpring(mouseY, {
                stiffness: 70,
                damping: 20,
                transform: (v) => v * 0.8,
              }).get() + 50,
              useSpring(mouseY, {
                stiffness: 70,
                damping: 20,
                transform: (v) => v * 0.8,
              }).get() - 30,
              useSpring(mouseY, {
                stiffness: 70,
                damping: 20,
                transform: (v) => v * 0.8,
              }),
            ],
            rotate: [0, 15, -10, 0],
          }}
          transition={{
            duration: 22,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />
      </div>

      {/* Main content */}
      <div className="relative z-10 flex items-center justify-center min-h-screen px-8">
        <div className="max-w-2xl">
          <motion.h1
            className={`text-6xl md:text-7xl font-light mb-8 ${
              darkMode ? "text-blue-200" : "text-blue-300"
            }`}
            style={{
              x: springX,
              y: springY,
            }}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, ease: "easeOut" }}
          >
            Hi, I'm Yazhen.
          </motion.h1>

          <motion.div
