import React, { useState, useEffect, useRef } from "react";
import { Routes, Route, Link } from "react-router-dom";
import { motion, useMotionValue, useSpring } from "framer-motion";

import Home from "./pages/Home";
import DigitalLandscapes from "./pages/DigitalLandscapes";
import PlaylistLocalization from "./pages/PlaylistLocalization";
import WebVerifyExpansion from "./pages/WebVerifyExpansion";
import UnifiedAuth from "./pages/UnifiedAuth";

export default function App() {
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("theme") === "dark";
  });

  const mouseX = useMotionValue(0);
  const mouseY = useMotionValue(0);

  const springX = useSpring(mouseX, { stiffness: 100, damping: 30 });
  const springY = useSpring(mouseY, { stiffness: 100, damping: 30 });

  // Create separate springs for each blob with different characteristics
  const pinkSpringX = useSpring(mouseX, { stiffness: 80, damping: 25 });
  const pinkSpringY = useSpring(mouseY, { stiffness: 80, damping: 25 });
  const blueSpringX = useSpring(mouseX, { stiffness: 90, damping: 35 });
  const blueSpringY = useSpring(mouseY, { stiffness: 90, damping: 35 });
  const purpleSpringX = useSpring(mouseX, { stiffness: 70, damping: 20 });
  const purpleSpringY = useSpring(mouseY, { stiffness: 70, damping: 20 });

  useEffect(() => {
    localStorage.setItem("theme", darkMode ? "dark" : "light");
  }, [darkMode]);

  const handleMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const centerX = rect.width / 2;
    const centerY = rect.height / 2;

    mouseX.set((e.clientX - rect.left - centerX) * 0.15);
    mouseY.set((e.clientY - rect.top - centerY) * 0.15);
  };

  return (
    <div
      className={`min-h-screen overflow-hidden ${
        darkMode ? "bg-gray-900" : "bg-gray-50"
      }`}
      onMouseMove={handleMouseMove}
    >
      {/* Dark mode toggle */}
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="fixed top-6 right-6 z-50 w-12 h-12 rounded-full border border-current flex items-center justify-center hover:bg-current hover:text-white transition-all duration-300"
      >
        {darkMode ? "‚òÄÔ∏è" : "üåô"}
      </button>

      {/* Abstract shapes */}
      <div className="fixed inset-0 pointer-events-none">
        {/* Yellow blob */}
        <motion.div
          className="absolute w-[600px] h-[600px] rounded-full"
          style={{
            background: "linear-gradient(135deg, #f3e8a6 0%, #e6d875 100%)",
            top: "-20%",
            left: "-20%",
            x: springX,
            y: springY,
          }}
          animate={{
            scale: [1, 1.2, 0.9, 1],
            rotate: [0, 10, -5, 0],
          }}
          transition={{
            duration: 15,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />

        {/* Pink blob with offset motion */}
        <motion.div
          className="absolute w-[500px] h-[700px]"
          style={{
            background: "linear-gradient(45deg, #f4c2c2 0%, #e8a5a5 100%)",
            borderRadius: "60% 40% 30% 70% / 60% 30% 70% 40%",
            top: "10%",
            left: "-15%",
          }}
          animate={{
            borderRadius: [
              "60% 40% 30% 70% / 60% 30% 70% 40%",
              "30% 60% 70% 40% / 50% 60% 30% 60%",
              "70% 30% 60% 40% / 40% 70% 60% 30%",
              "60% 40% 30% 70% / 60% 30% 70% 40%",
            ],
            x: [-60, 40, -20, -60],
            y: [30, -50, 10, 30],
            rotate: [0, -8, 12, 0],
          }}
          transition={{
            duration: 18,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />

        {/* Blue blob with different flow */}
        <motion.div
          className="absolute w-[550px] h-[600px]"
          style={{
            background: "linear-gradient(225deg, #b8d4f0 0%, #8bb5d9 100%)",
            borderRadius: "40% 60% 60% 40% / 60% 30% 70% 40%",
            bottom: "-10%",
            right: "-20%",
          }}
          animate={{
            borderRadius: [
              "40% 60% 60% 40% / 60% 30% 70% 40%",
              "60% 40% 40% 60% / 40% 60% 30% 70%",
              "50% 50% 50% 50% / 50% 50% 50% 50%",
              "40% 60% 60% 40% / 60% 30% 70% 40%",
            ],
            x: [80, -20, 60, 80],
            y: [-60, 40, -30, -60],
            scale: [1, 0.8, 1.1, 1],
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />

        {/* Purple blob */}
        <motion.div
          className="absolute w-[400px] h-[500px]"
          style={{
            background: "linear-gradient(180deg, #c8b8e8 0%, #b5a3d9 100%)",
            borderRadius: "70% 30% 50% 50% / 40% 60% 40% 60%",
            top: "40%",
            right: "10%",
          }}
          animate={{
            borderRadius: [
              "70% 30% 50% 50% / 40% 60% 40% 60%",
              "30% 70% 40% 60% / 60% 40% 60% 40%",
              "50% 50% 70% 30% / 50% 50% 30% 70%",
              "70% 30% 50% 50% / 40% 60% 40% 60%",
            ],
            x: [-40, 60, -10, -40],
            y: [50, -30, 20, 50],
            rotate: [0, 15, -10, 0],
          }}
          transition={{
            duration: 22,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />

        {/* Mouse-reactive overlay blobs */}
        <motion.div
          className="absolute w-[500px] h-[700px] opacity-30"
          style={{
            background: "linear-gradient(45deg, #f4c2c2 0%, #e8a5a5 100%)",
            borderRadius: "60% 40% 30% 70% / 60% 30% 70% 40%",
            top: "10%",
            left: "-15%",
            x: pinkSpringX,
            y: pinkSpringY,
            scale: 0.8,
          }}
        />

        <motion.div
          className="absolute w-[550px] h-[600px] opacity-30"
          style={{
            background: "linear-gradient(225deg, #b8d4f0 0%, #8bb5d9 100%)",
            borderRadius: "40% 60% 60% 40% / 60% 30% 70% 40%",
            bottom: "-10%",
            right: "-20%",
            x: blueSpringX,
            y: blueSpringY,
            scale: 0.7,
          }}
        />
      </div>

      {/* Main content */}
      <div className="relative z-10 flex items-center justify-center min-h-screen px-8">
        <div className="max-w-2xl">
          <motion.h1
            className={`text-6xl md:text-7xl font-light mb-8 ${
              darkMode ? "text-blue-200" : "text-blue-300"
            }`}
            style={{
              x: springX,
              y: springY,
            }}
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, ease: "easeOut" }}
          >
            Hi, I'm Yazhen.
          </motion.h1>

          <motion.div
            className={`space-y-4 text-lg leading-relaxed ${
              darkMode ? "text-gray-300" : "text-gray-700"
            }`}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: 0.3, ease: "easeOut" }}
          >
            <p>
              I'm currently a Sr product designer at{" "}
              <a href="#" className="underline hover:no-underline">
                CLEAR
              </a>{" "}
              working on a B2B console and the end user identity verification
              experiences. I have previously worked at Spotify, where I focused
              on building internal tooling, and at ICF Next in gov-tech.
            </p>
            <p>
              I love designing delightful experiences that can improve people's
              lives.
            </p>
            <a
              href="/resume.pdf"
              download
              className={`inline-block mt-6 ${
                darkMode ? "text-blue-300" : "text-blue-400"
              } underline hover:no-underline`}
            >
              Download my resume
            </a>
          </motion.div>
        </div>
      </div>

      {/* Case Studies Section */}
      <div className="relative z-10 px-8 pb-24">
        <div className="max-w-6xl mx-auto">
          <motion.h2
            className={`text-4xl font-light mb-12 text-center ${
              darkMode ? "text-blue-200" : "text-blue-300"
            }`}
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            Case Studies
          </motion.h2>

          {/* Bento Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 auto-rows-[200px]">
            {/* Digital Landscapes - Large */}
            <motion.div
              className={`md:col-span-2 lg:row-span-2 rounded-2xl p-6 border transition-all duration-300 hover:scale-[1.02] ${
                darkMode
                  ? "bg-gray-800/50 border-gray-700 hover:bg-gray-800/70"
                  : "bg-white/50 border-gray-200 hover:bg-white/70"
              }`}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.1 }}
              viewport={{ once: true }}
            >
              <Link
                to="/case-studies/digital-landscapes"
                className="block h-full"
              >
                <div className="flex flex-col justify-between h-full">
                  <div>
                    <h3
                      className={`text-2xl font-semibold mb-2 ${
                        darkMode ? "text-white" : "text-gray-900"
                      }`}
                    >
                      Digital Landscapes Redesign
                    </h3>
                    <p
                      className={`text-sm mb-4 ${
                        darkMode ? "text-gray-400" : "text-gray-500"
                      }`}
                    >
                      Walmart ¬∑ 2024‚Äì2025
                    </p>
                    <p
                      className={`text-sm leading-relaxed ${
                        darkMode ? "text-gray-300" : "text-gray-600"
                      }`}
                    >
                      Redesigned a key internal tool for supplier performance
                      data, improving navigation and user experience with
                      scalable components.
                    </p>
                  </div>
                  <div
                    className={`text-sm font-medium ${
                      darkMode ? "text-blue-300" : "text-blue-500"
                    }`}
                  >
                    +10.8% adoption increase ‚Üí
                  </div>
                </div>
              </Link>
            </motion.div>

            {/* Playlist Localization */}
            <motion.div
              className={`rounded-2xl p-6 border transition-all duration-300 hover:scale-[1.02] ${
                darkMode
                  ? "bg-gray-800/50 border-gray-700 hover:bg-gray-800/70"
                  : "bg-white/50 border-gray-200 hover:bg-white/70"
              }`}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              viewport={{ once: true }}
            >
              <Link
                to="/case-studies/playlist-localization"
                className="block h-full"
              >
                <div className="flex flex-col justify-between h-full">
                  <div>
                    <h3
                      className={`text-xl font-semibold mb-2 ${
                        darkMode ? "text-white" : "text-gray-900"
                      }`}
                    >
                      Playlist Localization Tool
                    </h3>
                    <p
                      className={`text-sm mb-3 ${
                        darkMode ? "text-gray-400" : "text-gray-500"
                      }`}
                    >
                      Spotify ¬∑ 2021‚Äì2022
                    </p>
                  </div>
                  <div
                    className={`text-sm font-medium ${
                      darkMode ? "text-blue-300" : "text-blue-500"
                    }`}
                  >
                    Global content scaling ‚Üí
                  </div>
                </div>
              </Link>
            </motion.div>

            {/* WebVerify Expansion */}
            <motion.div
              className={`rounded-2xl p-6 border transition-all duration-300 hover:scale-[1.02] ${
                darkMode
                  ? "bg-gray-800/50 border-gray-700 hover:bg-gray-800/70"
                  : "bg-white/50 border-gray-200 hover:bg-white/70"
              }`}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
              viewport={{ once: true }}
            >
              <Link
                to="/case-studies/webverify-expansion"
                className="block h-full"
              >
                <div className="flex flex-col justify-between h-full">
                  <div>
                    <h3
                      className={`text-xl font-semibold mb-2 ${
                        darkMode ? "text-white" : "text-gray-900"
                      }`}
                    >
                      WebVerify i18n Expansion
                    </h3>
                    <p
                      className={`text-sm mb-3 ${
                        darkMode ? "text-gray-400" : "text-gray-500"
                      }`}
                    >
                      CLEAR ¬∑ 2021
                    </p>
                  </div>
                  <div
                    className={`text-sm font-medium ${
                      darkMode ? "text-blue-300" : "text-blue-500"
                    }`}
                  >
                    98.8% error reduction ‚Üí
                  </div>
                </div>
              </Link>
            </motion.div>

            {/* Unified Auth - Wide */}
            <motion.div
              className={`md:col-span-2 rounded-2xl p-6 border transition-all duration-300 hover:scale-[1.02] ${
                darkMode
                  ? "bg-gray-800/50 border-gray-700 hover:bg-gray-800/70"
                  : "bg-white/50 border-gray-200 hover:bg-white/70"
              }`}
              initial={{ opacity: 0, y: 40 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              viewport={{ once: true }}
            >
              <Link to="/case-studies/unified-auth" className="block h-full">
                <div className="flex flex-col justify-between h-full">
                  <div>
                    <h3
                      className={`text-2xl font-semibold mb-2 ${
                        darkMode ? "text-white" : "text-gray-900"
                      }`}
                    >
                      Unified Authentication
                    </h3>
                    <p
                      className={`text-sm mb-4 ${
                        darkMode ? "text-gray-400" : "text-gray-500"
                      }`}
                    >
                      CLEAR ¬∑ 2023‚Äì2024
                    </p>
                    <p
                      className={`text-sm leading-relaxed ${
                        darkMode ? "text-gray-300" : "text-gray-600"
                      }`}
                    >
                      Streamlined authentication across multiple platforms and
                      user types.
                    </p>
                  </div>
                  <div
                    className={`text-sm font-medium ${
                      darkMode ? "text-blue-300" : "text-blue-500"
                    }`}
                  >
                    Cross-platform unification ‚Üí
                  </div>
                </div>
              </Link>
            </motion.div>
          </div>
        </div>
      </div>

      <Routes>
        <Route path="/" element={<div />} />
        <Route path="/projects" element={<Home />} />
        <Route
          path="/case-studies/digital-landscapes"
          element={<DigitalLandscapes />}
        />
        <Route
          path="/case-studies/playlist-localization"
          element={<PlaylistLocalization />}
        />
        <Route
          path="/case-studies/webverify-expansion"
          element={<WebVerifyExpansion />}
        />
        <Route path="/case-studies/unified-auth" element={<UnifiedAuth />} />
      </Routes>
    </div>
  );
}
