import React, { useState, useEffect } from "react";
import { Routes, Route, Link } from "react-router-dom";
import { motion } from "framer-motion";

import Home from "./pages/Home";
import DigitalLandscapes from "./pages/DigitalLandscapes";
import PlaylistLocalization from "./pages/PlaylistLocalization";
import WebVerifyExpansion from "./pages/WebVerifyExpansion";
import UnifiedAuth from "./pages/UnifiedAuth";
import WaveBackground from "./components/WaveBackground";

export default function App() {
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("theme") === "dark";
  });

  useEffect(() => {
    localStorage.setItem("theme", darkMode ? "dark" : "light");
  }, [darkMode]);

  return (
    <div className={darkMode ? "bg-black text-white" : "bg-white text-black"}>
      <WaveBackground darkMode={darkMode} />
      <div className="relative z-10">
        <header className="h-screen flex flex-col items-center justify-center text-center px-4">
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="absolute top-6 right-6 px-4 py-2 rounded-full border"
          >
            {darkMode ? "Light Mode" : "Dark Mode"}
          </button>

          <motion.h1
            className="text-6xl font-extrabold tracking-tight"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            Yaz Liu
          </motion.h1>
          <motion.p
            className="mt-6 text-lg max-w-xl"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.4, duration: 0.8 }}
          >
            Product designer focused on turning complexity into clarity. I build
            systems, elevate design ops, and scale meaningful experiences.
          </motion.p>
          <a
            href="/resume.pdf"
            download
            className="mt-8 inline-block px-6 py-2 border rounded-full text-sm hover:bg-gray-200 hover:text-black"
          >
            Download Resume
          </a>
        </header>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route
            path="/case-studies/digital-landscapes"
            element={<DigitalLandscapes />}
          />
          <Route
            path="/case-studies/playlist-localization"
            element={<PlaylistLocalization />}
          />
          <Route
            path="/case-studies/webverify-expansion"
            element={<WebVerifyExpansion />}
          />
          <Route path="/case-studies/unified-auth" element={<UnifiedAuth />} />
        </Routes>
      </div>

      <footer className="text-center text-sm py-8 border-t">
        <p className="text-gray-400">&copy; 2025 Yaz Liu</p>
      </footer>
    </div>
  );
}
